INSERT INTO `pages` (`id`, `title`, `slug`, `content`, `user_id`, `status`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'Giới thiệu', 'gioi-thieu', '<p><strong>ISMART L&Agrave; NỀN TẢNG THƯƠNG MẠI ĐIỆN TỬ H&Agrave;NG ĐẦU TẠI Đ&Ocirc;NG NAM &Aacute; V&Agrave; Đ&Agrave;I LOAN.</strong><br><img src=\"/Vsmart/public//storage/photos/21/post/about.jpg\" alt=\"\" width=\"474\" height=\"474\"></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\"><strong>Vsmart</strong> l&agrave; một hệ sinh th&aacute;i thương mại tất cả trong một, gồm c&aacute;c c&ocirc;ng ty th&agrave;nh vi&ecirc;n như:</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\"><strong>- C&ocirc;ng ty TNHH TI KI</strong> (\"<strong>Vsmart</strong>\") l&agrave; đơn vị thiết lập, tổ chức s&agrave;n thương mại điện tử www.Vsmart.vn để c&aacute;c Nh&agrave; b&aacute;n h&agrave;ng thể tiến h&agrave;nh một phần hoặc to&agrave;n bộ quy tr&igrave;nh mua b&aacute;n h&agrave;ng h&oacute;a, dịch vụ tr&ecirc;n s&agrave;n thương mại điện tử.</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\"><strong>- C&ocirc;ng ty TNHH Vsmart Smart Logistics (\"TNSL\")</strong> l&agrave; đơn vị cung cấp c&aacute;c dịch vụ logistics đầu-cuối, dịch vụ vận chuyển, dịch vụ bưu ch&iacute;nh cho S&agrave;n thương mại điện tử www.Vsmart.vn - C&ocirc;ng ty TNHH MTV Thương mại Vsmart (\"VsmartTrading\") l&agrave; đơn vị b&aacute;n h&agrave;ng h&oacute;a, dịch vụ tr&ecirc;n s&agrave;n thương mại điện tử</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">- Đơn vị b&aacute;n lẻ Vsmart Trading v&agrave; S&agrave;n Giao dịch cung cấp 10 triệu sản phẩm từ 26 ng&agrave;nh h&agrave;ng phục vụ h&agrave;ng triệu kh&aacute;ch h&agrave;ng tr&ecirc;n to&agrave;n quốc.</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">Với phương ch&acirc;m hoạt động &ldquo;Tất cả v&igrave; Kh&aacute;ch H&agrave;ng&rdquo;, Vsmartlu&ocirc;n kh&ocirc;ng ngừng nỗ lực n&acirc;ng cao chất lượng dịch vụ v&agrave; sản phẩm, từ đ&oacute; mang đến trải nghiệm mua sắm trọn vẹn cho Kh&aacute;ch H&agrave;ng Việt Nam với dịch vụ giao h&agrave;ng nhanh trong 2 tiếng v&agrave; ng&agrave;y h&ocirc;m sau Vsmart lần đầu ti&ecirc;n tại Đ&ocirc;ng Nam &Aacute;, c&ugrave;ng cam kết cung cấp h&agrave;ng ch&iacute;nh h&atilde;ng với ch&iacute;nh s&aacute;ch ho&agrave;n tiền 111% nếu ph&aacute;t hiện h&agrave;ng giả, h&agrave;ng nh&aacute;i.</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">Th&agrave;nh lập từ th&aacute;ng 3/2010, Vsmart.vn hiện đang l&agrave; trang thương mại điện tử lọt top 2 tại Việt Nam v&agrave; top 6 tại khu vực Đ&ocirc;ng Nam &Aacute;.</span></p>\r\n<p class=\"MsoNormal\"><span lang=\"EN-US\">Vsmart lọt Top 1 nơi l&agrave;m việc tốt nhất Việt Nam trong ng&agrave;nh Internet/E-commerce 2018 (Anphabe b&igrave;nh chọn), Top 50 nơi l&agrave;m việc tốt nhất ch&acirc;u &Aacute; 2019 (HR Asia b&igrave;nh chọn).</span></p>', 21, 1, '2023-03-16 07:59:20', '2023-03-31 17:43:41', NULL),
(3, 'Liên hệ', 'lien-he', '<p>Tư vấn, Cskh: 0989.989.989</p>\r\n<p>Số điện thoại li&ecirc;n hệ : 0987.654.321</p>\r\n<p>Email li&ecirc;n hệ: <span class=\"qu\" tabindex=\"-1\" role=\"gridcell\" translate=\"no\"><span class=\"go\">vsmartshop01@gmail.com</span></span></p>\r\n<p>&nbsp;</p>\r\n<div class=\"ddict_btn\" style=\"top: 133px; left: 1015.98px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\"></div>\r\n<div class=\"ddict_btn\" style=\"top: 141px; left: 1015.98px;\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\"></div>', 21, 1, '2023-03-16 08:15:32', '2023-03-31 17:42:21', NULL),
